import React, { useState } from 'react'

const NeedleForm = props => {

const [size, setSize] = useState(props.needleData.size)
const [straight, setStraight] = useState(props.needleData.straight)
const [circular, setCircular] = useState(props.needleData.circular)
const [doublepoint, setDoublepoint] = useState(props.needleData.doublepoint)
const [id, setId] = useState(props.needleData.id)


    const handleChangeNeedleCheck = (e) => {
        setState({ [e.target.id]: e.target.checked })
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        const needleinfo = {
            size:size,
            straight:straight,
            circular:circular,
            id:id
        }
        props.handleUpdateNeedle(needleinfo)
        setSize('')
        setStraight('')
        setCircular('')
        setDoublepoint('')
    }

        return (

            <form onSubmit={handleSubmit}>
            {size}<br/>
            <label>
            Straight
            {(straight==='1')
            ?
            <input type="checkbox" id="straight" value={straight} onChange={handleChangeNeedleCheck} checked/>
            :
            <input type="checkbox" id="straight" value='' onChange={handleChangeNeedleCheck}/>
            }
            </label>
            <label id="circular">
            Circular
            {(circular==='1')
            ?
            <input type="checkbox" id="circular" value={circular} onChange={handleChangeNeedleCheck} checked/>
            :
            <input type="checkbox" id="circular" value='' onChange={handleChangeNeedleCheck}/>}
            </label>
            <label id="doublepoint">
            Double-point
            {(doublepoint==='1')
            ?
            <input type="checkbox" id="doublepoint" value={doublepoint} onChange={handleChangeNeedleCheck} checked/>
            :
            <input type="checkbox" id="doublepoint" value='' onChange={handleChangeNeedleCheck}/>}
            </label>
            <input type="submit" value="Apply Changes" onClick={props.handleClose}/>
            </form>

        )
}

// =============================
// EXPORT
// =============================
export default NeedleForm
